        /*First Icon Effect */
        
        .icon-effect-1 {
            background-color: #fff;
            transition: color 0.4s;
        }
        
        .gray-icon.icon-effect-1 {
            box-shadow: 0 0 0 3px #444;
            -o-box-shadow: 0 0 0 3px #444;
            -moz-box-shadow: 0 0 0 3px #444;
            -webkit-box-shadow: 0 0 0 3px #444;
        }
        
        .icon-effect-1:after {
            top: -2px;
            left: -2px;
            padding: 2px;
            z-index: -1;
            transition: transform 0.4s, opacity 0.4s;
        }
        
        .gray-icon.icon-effect-1:after {
            background: #444;
        }
        
        .icon-effect-1.gray-icon:hover,
        .service-box:hover .gray-icon.icon-effect-1 {
            color: #444;
        }
        
        .icon-effect-1:hover:after,
        .service-box:hover .icon-effect-1:after {
            transform: scale(1.3);
            opacity: 0;
        }
        /*Seconed Icon Effect */
        
        .icon-effect-2 {
            background: #fff;
            transition: color 0.4s;
        }
        
        .gray-icon.icon-effect-2 {
            color: #444;
            box-shadow: 0 0 0 3px #444;
            -o-box-shadow: 0 0 0 3px #444;
            -moz-box-shadow: 0 0 0 3px #444;
            -webkit-box-shadow: 0 0 0 3px #444;
        }
        
        .icon-effect-2:after {
            top: -2px;
            left: -2px;
            padding: 2px;
            z-index: -1;
            transform: scale(1.3);
            opacity: 0;
            transition: transform 0.4s, opacity 0.4s;
        }
        
        .gray-icon.icon-effect-2:after {
            background: #444;
        }
        
        .icon-effect-2:hover,
        .service-box:hover .icon-effect-2,
        .icon-effect-2.gray-icon:hover,
        .service-box:hover .gray-icon.icon-effect-2 {
            color: #fff;
        }
        
        .icon-effect-2:hover:after,
        .service-box:hover .icon-effect-2:after {
            transform: scale(1);
            opacity: 1;
        }
        /*Third Icon Effect */
        
        .icon-effect-3 {
            background: #fff;
        }
        
        .gray-icon.icon-effect-3 {
            color: #444;
            box-shadow: 0 0 0 3px #444;
            -o-box-shadow: 0 0 0 3px #444;
            -moz-box-shadow: 0 0 0 3px #444;
            -webkit-box-shadow: 0 0 0 3px #444;
        }
        
        .icon-effect-3:hover:before,
        .service-box:hover .icon-effect-3:before {
            -webkit-animation: toRightFromLeft 0.4s forwards;
            animation: toRightFromLeft 0.4s forwards;
        }
        
        @-webkit-keyframes toRightFromLeft {
            49% {
                -webkit-transform: translate(100%);
            }
            50% {
                opacity: 0;
                -webkit-transform: translate(-100%);
            }
            51% {
                opacity: 1;
            }
        }
        
        @keyframes toRightFromLeft {
            49% {
                transform: translate(100%);
            }
            50% {
                opacity: 0;
                transform: translate(-100%);
            }
            51% {
                opacity: 1;
            }
        }
        /*Fourth Icon Effect */
        
        .icon-effect-4 {
            background: #fff;
